  }
  var nullInputFormatter = [
    FilteringTextInputFormatter.deny(RegExp(r'^\s')),
    FilteringTextInputFormatter.deny(RegExp(emojiRestictedRegrestion)),
  ];
  if (keyboardType == TextInputType.number && zeroEnterDisable) {
    nullInputFormatter.add(FilteringTextInputFormatter.allow(RegExp(r'[0-9]')));
    nullInputFormatter.add(FilteringTextInputFormatter.deny(
      RegExp(r'^0+'),
    ));
  } else {
    if (keyboardType == TextInputType.number) {
      nullInputFormatter
          .add(FilteringTextInputFormatter.allow(RegExp(r'[0-9]')));
    } else {
