        nullInputFormatter
            .add(FilteringTextInputFormatter.allow(RegExp(r'^[0-9.]*$')));
      } else {
        if (keyboardType ==
            const TextInputType.numberWithOptions(signed: true)) {
          nullInputFormatter
              .add(FilteringTextInputFormatter.allow(RegExp(r'^[0-9.]*$')));
        } else if (keyboardType ==
                const TextInputType.numberWithOptions(decimal: true) &&
            zeroEnterDisable) {
          nullInputFormatter.add(FilteringTextInputFormatter.deny(
            RegExp(r'[^\d\.]'),
          ));
          nullInputFormatter.add(FilteringTextInputFormatter.deny(
            RegExp(r'(\.\.)|(\.[^\d])'),
          ));
          nullInputFormatter.add(FilteringTextInputFormatter.deny(
            RegExp(r'^0+'),
          ));
        } else if (keyboardType ==
            const TextInputType.numberWithOptions(decimal: true)) {
          nullInputFormatter.add(FilteringTextInputFormatter.deny(
            RegExp(r'[^\d\.]'),
          ));
          nullInputFormatter.add(FilteringTextInputFormatter.deny(
            RegExp(r'(\.\.)|(\.[^\d])'),
          ));
        } else {
          print('inside else else else part');
        }
      }
    }
  }
  var maxLengthh = 50;
  if (maxLength == null) {
    if (keyboardType == TextInputType.number ||
        keyboardType == TextInputType.numberWithOptions(signed: true)) {
      maxLengthh = 15;
    }
  } else {
    maxLengthh = maxLength;
