                        textInputAction:
                            textInputAction ?? TextInputAction.done,
                        controller: controller,
                        maxLines: maxLines ?? 1,
                        minLines: 1,
                        maxLength: maxLengthh,
                        // onChanged: onChanged,
                        onChanged: (value) {
                          if (zeroEnterDisable == false ||
                              (keyboardType == TextInputType.number &&
                                  keyboardType !=
                                      TextInputType.numberWithOptions(
                                          decimal: true))) {
                            if (value.length > 1) {
                              if (value[0] == '0') {
                                int sum = 0;
                                for (int i = 0; i < value.length; i++) {
                                  if (i != 0 || value[i] != '0') {
                                    sum += int.parse(value[i]);
                                  }
                                }
                                controller.text = sum.toString();
                              }
                            }
                          }
                          onChanged?.call(value);
                        },
                        expands: false,
                        obscureText: !passwordVisible,
                        readOnly: readOnly ?? false,
                        autofocus: autoFocus ?? false,
                        onTap: () {
                          focusNode.requestFocus();
                          onClick?.call();
                        },
                        focusNode: focusNode,
                        onFieldSubmitted: (value) {
                          onFieldSubmit?.call();
                        },
                        decoration: InputDecoration(
                          filled: true,
                          fillColor: lightColorPalette.lightBlue,
                          isCollapsed: true,
                          isDense: true,
                          counterText: "",
                          border: InputBorder.none,
                          hintText: !focusNode.hasFocus ? hint : '',
                          hintStyle: CustomTextTheme.normalTextStyle(
                              color: lightColorPalette.grey),
                        ),
                        style: CustomTextTheme.normalTextStyle(),
                      ),
                    ),
                    showClearIcon
                        ? CustomTapState(
                            onTap: () => onClearIconClick?.call(),
                            child: Center(
                                child: Padding(
                                    padding: EdgeInsets.only(right: 5.w),
                                    child: Icon(
                                      Icons.clear,
                                      size: 22.w,
                                      color: lightColorPalette.black,
                                    ))))
                        : const SizedBox()
                  ],
                )),
          ),
        ),
      ],
    ).marginSymmetric(horizontal: 2.5.w),
  );
